import java.io.*;
import java.util.*;
import java.math.*;
public class RadiusOfCircles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner circleReader=new Scanner(System.in);
		System.out.println("What is the area?_ ");
		double a=circleReader.nextDouble();
		double area=(Math.sqrt(a/Math.PI));
		System.out.println("The radius of you circle is: " + area);

	}


	}


